xwikidoc.content:
== Import Page ==

{{groovy}}
import com.xpn.xwiki.*;
import com.xpn.xwiki.doc.*;
import com.xpn.xwiki.plugin.packaging.*;

import java.util.zip.*;

finalPageName = '';
finalPageURL = '';

def updateDoc(String newname, String xml, String type, XWikiContext context) {
  def prevDoc =  context.getWiki().getDocument(newname, context);
  def prevArchive = prevDoc.getDocumentArchive();
  if (prevDoc.isNew() || ((type != 'NOOVERVERSIONS') && (type != 'NOOVERNOVERSIONS'))) {
    def fdoc = new XWikiDocument();
    fdoc.fromXML(xml);
    // We need to verify the document has the right name
    // Otherwise we might override something we don't expect
    if ((newname != '') && (fdoc.fullName != newname)) {
      fdoc = fdoc.copyDocument(newname, context);
    }
    finalPageName = fdoc.getFullName();
    finalPageURL = fdoc.getURL('view', context);

    if (type == 'ADDVERSION') {
      fdoc.setDocumentArchive(prevArchive);
      context.getWiki().saveDocument(fdoc, context);
      if (prevDoc.isNew()) {
        fdoc.resetArchive(context);
      }
      return 1;
    } else {
      def pack = new Package();
      if ((type == 'NOOVERVERSIONS') || (type == 'OVERVERSIONS')) {
        pack.setWithVersions(true);
      }
      else {
        pack.setWithVersions(false);
      }
      pack.setBackupPack(true);
      pack.add(fdoc, 0, context);
      return pack.install(context);
    }
  } else {
    return -1000;
  }
}

if (request.get('name') != null) {
  def fupl = xwiki.get('fileupload');
  def docName = fupl.getFileItem('name');
  def type = fupl.getFileItem('type');
  def data = fupl.getFileItemData("file");
  def sb = new StringBuilder();

  if ((data != null) && (data.length > 0)) {
    def bais = new ByteArrayInputStream(data);
    def zis = new ZipInputStream(bais);
    def buffer = new byte[1024];
    while ((entry = zis.getNextEntry()) != null) {
      if (entry.getName().compareTo('package.xml') != 0) {
        while ((read = zis.read(buffer, 0, 1024)) >= 0) {
          sb.append(new String(buffer, 0, read));
        }
      }
    }
  }
  def ret = updateDoc(docName, sb.toString(), type, xcontext.context);
  if (ret < 0) {
    if (ret==-1000)
      println 'Document already exists and overwrite was not specified.';
    else
      println "Document could not be imported with error ${ret}.";
  } else {
    println "Document has been imported properly: [[${finalPageName}>>path:${finalPageURL}]].";
  }
} else {
  println """
    {{html clean='false' wiki='true'}}
      <form action="" enctype="multipart/form-data" method="post">
        <table border="0">
          <tr>
            <td>
              Page Name to save to:</td><td><input type="text" name="name" size="60" />
            </td>
          </tr>
          <tr>
            <td>
              Type:
            </td>
            <td>
              <input type="radio" name="type" value="NOOVERVERSIONS" checked /> Do Not overwrite with versions
              <input type="radio" name="type" value="NOOVERNOVERSIONS" /> Do Not overwrite without versions
              <br />
              <input type="radio" name="type" value="ADDVERSION" /> Add version
              <input type="radio" name="type" value="OVERVERSIONS" /> Overwrite with version
              <input type="radio" name="type" value="OVERNOVERSIONS" /> Overwrite without versions
            </td>
