{{groovy}}
if (request.lines) {
  lines = request.lines;
} else {
  lines = 2000
}
if (request.logfile) {
  logfile = request.logfile;
} else {
  logfile = "/usr/local/tomcat/logs/catalina.out"
}
{{/groovy}}

{{info}}This script only works in a Linux environnent.{{/info}}
