{{groovy}}
if(request.showlog == "1") {
  print("= Result =")
  line = "/usr/bin/tail -n " + lines + " " + logfile
  print("{{code language='none'}}")
  print(line.execute().getText()) 
  print("{{/code}}")
}
{{/groovy}}
